#define MON_STATS(hp, atk, def, speed, spAtk, spDef) \
    .baseHP        = hp,                             \
    .baseAttack    = atk,                            \
	.baseDefense   = def,                            \
    .baseSpeed     = speed,                          \
    .baseSpAttack  = spAtk,                          \
	.baseSpDefense = spDef

#define MON_TYPES(type1, ...) .types = { type1, DEFAULT(type1, __VA_ARGS__) }

#define MON_ABILITIES(ability1, ...) .abilities = { ability1, DEFAULT(ability1, __VA_ARGS__) }

// Maximum value for a female Pokémon is 254 (MON_FEMALE) which is 100% female.
// 255 (MON_GENDERLESS) is reserved for genderless Pokémon.
#define PERCENT_FEMALE(percent) min(MON_FEMALE, ((percent * 255) / 100))

#define MON_EGG_GROUPS(group1, ...)           \
    .eggGroup1 = group1,                      \
	.eggGroup2 = DEFAULT(group1, __VA_ARGS__)

#define FOOTPRINT(sprite) .footprint = sMonFootprint_##sprite

#define MON_ICON(sprite) .icon = sMonIcon_##sprite

#define SOS_ALLIES(...) { __VA_ARGS__ }

#define EVOLUTION(...) (const u8[]) { __VA_ARGS__, EVOLUTIONS_END }

